// Appointments Page javascript codes
$(document).ready(function () {
  function renderPagination(current, total) {
    const container = $('#paginationControls');
    container.empty();

    if (total <= 1) return;

    const createButton = (pageNum, isActive = false) => {
      const btn = $(`<button class="btn btn-sm ${isActive ? 'btn-primary' : 'btn-outline-primary'} mx-1">${pageNum}</button>`);
      btn.on('click', () => loadAppointments(currentStatus, pageNum));
      return btn;
    };

    const addEllipsis = () => {
      container.append('<span class="mx-2">...</span>');
    };

    // Always show: First, Prev
    if (current > 1) container.append(createButton(current - 1, false).text('Â« Prev'));
    container.append(createButton(1, current === 1));

    if (current > 3) addEllipsis();

    // Show current, previous, and next (middle pages)
    for (let i = current - 1; i <= current + 1; i++) {
      if (i > 1 && i < total) {
        container.append(createButton(i, i === current));
      }
    }

    if (current < total - 2) addEllipsis();

    // Always show: Last, Next
    if (total > 1) container.append(createButton(total, current === total));
    if (current < total) container.append(createButton(current + 1, false).text('Next Â»'));
  }



  
  let currentStatus = 'pending';
  let currentPage = 1;
  const itemsPerPage = 5; // You can adjust per page limit

  function loadAppointments(status, page = 1) {
    currentStatus = status;
    currentPage = page;

    $.ajax({
      url: `/appointments/status/${status}?page=${page}&limit=${itemsPerPage}`,
      method: 'GET',
      success: function (res) {
        const data = res.data || [];
        const currentPage = res.currentPage || 1;
        const totalPages = res.totalPages || 1;
        const totalItems = res.totalItems || 0;

        let html = '';
        if (totalItems === 0) {
          html = `<div class="p-4 text-center">No ${status} appointments found.</div>`;
        } else {
          html += `<div class="px-3 py-2 fw-bold fs-4">Appointments: ${totalItems}</div>`;
          data.forEach((item, index) => {
            const shortMessage = item.message.length > 200 ? item.message.substring(0, 200) + '...' : item.message;
            let acceptBtn = '';
            if (item.status === 'pending') {
              acceptBtn = item.acceptedOrNot === 'accepted'
                ? `<button class="btn btn-gray btn-sm" disabled>Accepted</button>`
                : `<button class="btn btn-secondary btn-sm accept-btn" data-id="${item._id}">Accept</button>`;
            }

            html += `
              <div class="d-flex flex-row comment-row border-bottom p-3 gap-3 appointment-card" 
                  data-index="${(page - 1) * itemsPerPage + index}">
                <div>
                  <span><img src="/dashboard-assets/images/profile/user-3.jpg" class="rounded-circle" alt="user" width="50" /></span>
                </div>
                <div class="comment-text w-100">
                  <h6 class="fw-medium">${item.name}</h6>
                  <p class="mb-1 fs-2 text-muted">${shortMessage}</p>
                  <div class="comment-footer mt-2 d-flex justify-content-between align-items-center">
                    ${acceptBtn} 
                    <span class="update-status-btn badge bg-${status === 'pending' ? 'primary' : status === 'attended' ? 'success' : 'danger'} text-white cursor-pointer" 
                      data-id="${item._id}" 
                      data-status="${item.status}">
                      ${status.charAt(0).toUpperCase() + status.slice(1)}
                    </span>
                    ${status === 'pending'
                      ? `<button class="btn btn-warning btn-sm ms-2 reschedule-btn" data-id="${item._id}">Reschedule</button>`
                      : ''}
                  </div>
                  <span class="text-muted ms-auto fw-normal fs-2 d-block mt-2 text-end">
                    Date Booked: ${formatDate(item.dateBooked)}
                  </span>
                </div>
              </div>
            `;
          });

          $('#appointmentList').data('appointments', data);
        }

        $('#appointmentList').html(html);

        // âœ¨ Render Pagination
        renderPagination(currentPage, totalPages);

        // Your existing handlers...
        $('.appointment-card').on('click', function () {
          const index = $(this).data('index');
          const item = $('#appointmentList').data('appointments')[index];

          Swal.fire({
            title: `<strong>${item.name}</strong>`,
            html: `
              <div style="text-align: left;">
                <p><strong>Email:</strong> ${item.email || 'N/A'}</p>
                <p><strong>Phone:</strong> ${item.phone || 'N/A'}</p>
                <p><strong>Department:</strong> ${item.department || 'N/A'}</p>
                <p><strong>Doctor:</strong> ${item.doctor || 'N/A'}</p>
                <p><strong>Message:</strong> ${item.message || 'N/A'}</p>
                <p><strong>Status:</strong> ${item.status}</p>
                <p><strong>Date Booked:</strong> ${formatDate(item.dateBooked)}</p>
              </div>
            `,
            icon: 'info',
            confirmButtonText: 'Close',
            customClass: { popup: 'text-start' }
          });
        });

        $('.update-status-btn').on('click', function (e) {
          e.stopPropagation();
          const status = $(this).data('status');
          const id = $(this).data('id');

          if (status !== 'pending') return;

          Swal.fire({
            title: 'Update Appointment Status',
            text: 'Do you want to mark this as completed or rejected?',
            icon: 'question',
            showDenyButton: true,
            showCancelButton: true,
            confirmButtonText: 'Completed',
            denyButtonText: 'Reject',
            cancelButtonText: 'Cancel',
            customClass: {
              confirmButton: 'btn-completed',
              denyButton: 'btn-rejected',
              cancelButton: 'btn-cancel'
            },
            buttonsStyling: false
          }).then((result) => {
            if (result.isConfirmed) {
              updateAppointmentStatus(id, 'attended');
            } else if (result.isDenied) {
              updateAppointmentStatus(id, 'rejected');
            }
          });
        });
      },
      error: function () {
        Swal.fire({
          title: 'Error!',
          text: 'Something went wrong while loading appointments.',
          icon: 'error',
          confirmButtonText: 'OK'
        });
        $('#appointmentList').html('<div class="p-4 text-danger text-center">Error loading appointments.</div>');
      }
    });
  }



  function updateAppointmentStatus(id, newStatus) {
    // âœ… Show loading spinner before AJAX request
    Swal.fire({
      title: 'Updating Status...',
      text: 'This will take a few seconds...',
      allowOutsideClick: false,
      didOpen: () => {
        Swal.showLoading(); // shows spinner
      }
    });
    $.ajax({
      url: `/appointments/${newStatus}/${id}`,
      method: 'POST',
      success: function (res) {
        Swal.fire({
          title: 'Success!',
          text: res.message || 'Appointment updated.',
          icon: 'success',
          timer: 2000,
          showConfirmButton: false
        });
        loadAppointments(currentStatus);
      },
      error: function (err) {
        Swal.fire({
          title: 'Error!',
          text: err.responseJSON?.message || 'Failed to update appointment.',
          icon: 'error'
        });
      }
    });
  }

  // Load default
  loadAppointments('pending');

  // Navigation buttons
  $('#btnPending').on('click', () => loadAppointments('pending'));
  $('#btnAttended').on('click', () => loadAppointments('attended'));
  $('#btnRejected').on('click', () => loadAppointments('rejected'));



  // Reschedule button handler
  function updateAppointmentDate(appointmentId, newDate) {
    // âœ… Show loading spinner before AJAX request
    Swal.fire({
      title: 'Updating Date...',
      text: 'Notifying doctor, patient & admin...',
      allowOutsideClick: false,
      didOpen: () => {
        Swal.showLoading(); // shows spinner
      }
    });
    $.ajax({
      url: `/appointments/update-date/${appointmentId}`,
      method: 'POST',
      data: { newDate },
      success: function (res) {
        Swal.fire({
          title: 'Date Updated!',
          text: 'Notifications sent to doctor, patient, and admin.',
          icon: 'success',
          timer: 2500,
          showConfirmButton: false
        });
        loadAppointments(currentStatus); // Reload list
      },
      error: function (err) {
        Swal.fire({
          title: 'Error!',
          text: err.responseJSON?.message || 'Failed to update date.',
          icon: 'error'
        });
      }
    });
  }

  $('#appointmentList').on('click', '.reschedule-btn', function (e) {
    e.stopPropagation(); // Prevent card popup
    const appointmentId = $(this).data('id');

    Swal.fire({
      title: 'Select New Appointment Date',
      html: `<input type="datetime-local" id="newDate" class="form-control text-black">`,
      confirmButtonText: 'Proceed',
      focusConfirm: false,
      preConfirm: () => {
        const selectedDate = $('#newDate').val();
        if (!selectedDate) {
          Swal.showValidationMessage('Please select a date');
          return false;
        }
        return selectedDate;
      }
    }).then((result) => {
      if (result.isConfirmed) {
        const selectedDate = result.value;

        // Confirm notifications
        Swal.fire({
          title: 'Are you sure?',
          html: `Changing this appointment date will notify:<br>
                The Doctor,<br>
                The Patient. and<br>
                You (The Admin)<br><br>
                <b>New Date:</b> ${formatDate(selectedDate)}`,
          icon: 'warning',
          showCancelButton: true,
          confirmButtonText: 'Yes, Change & Notify',
          cancelButtonText: 'Cancel'
        }).then((confirmResult) => {
          if (confirmResult.isConfirmed) {
            updateAppointmentDate(appointmentId, selectedDate);
          }
        });
      }
    });
  });



  // Accept appointment and forward necessary messages
  $('#appointmentList').on('click', '.accept-btn', function (e) {
    e.stopPropagation();
    const appointmentId = $(this).data('id');

    Swal.fire({
      title: 'Accept Appointment?',
      html: `
        <p>Do you want to <strong>accept</strong> this appointment?</p>
        <p>This will notify:</p>
          The Doctor,<br>
          The Patient. and<br>
          You (The Admin)<br><br>
        <p>Appointment will be set with the current date. Unless you wish to reschedule.</p>
      `,
      icon: 'question',
      showCancelButton: true,
      confirmButtonText: 'Yes, Accept & Notify',
      cancelButtonText: 'Cancel'
    }).then((result) => {
      if (result.isConfirmed) {

        // âœ… Show loading spinner while AJAX is running
        Swal.fire({
          title: 'Processing...',
          text: 'Notifying doctor, patient & admin...',
          allowOutsideClick: false,
          didOpen: () => {
            Swal.showLoading(); // show spinner
          }
        });

        $.ajax({
          url: `/appointments/accept/${appointmentId}`,
          method: 'POST',
          success: function (res) {
            Swal.fire({
              title: 'âœ… Accepted!',
              text: res.message,
              icon: 'success',
              timer: 2500,
              showConfirmButton: false
            });
            loadAppointments(currentStatus);
          },
          error: function (err) {
            Swal.fire({
              title: 'âŒ Error!',
              text: err.responseJSON?.message || 'Failed to accept appointment.',
              icon: 'error'
            });
          }
        });
      }
    });
  });


});









// Departments page JavaScript Codes

$(document).ready(function() {
  fetchDepartments();


  // Preview for Create
  $('#deptImage').on('change', function (event) {
    const preview = $('#previewDeptPic');
    const file = event.target.files[0];
    if (file) {
      preview.attr('src', URL.createObjectURL(file)).removeClass('d-none');
    } else {
      preview.addClass('d-none').attr('src', '');
    }
  });

  // Handle form submission
  $('#departmentForm').on('submit', function(e) {
    e.preventDefault();
    const form = $('#departmentForm')[0];
    const formData = new FormData(form);

    const name = $('#deptName').val().trim();
    if (!name) {
      return Swal.fire({
        icon: 'warning',
        title: 'Missing Name',
        text: 'Please enter a department name.'
      });
    }


    // âœ… Show loading spinner before AJAX request
    Swal.fire({
      title: 'Creating Department...',
      text: 'Please wait ...',
      allowOutsideClick: false,
      didOpen: () => {
        Swal.showLoading(); // shows spinner
      }
    });

    $.ajax({
      url: '/departments/createDepartment',
      method: 'POST',
      data: formData,
      processData: false,
      contentType: false,
    }).done(function(response) {
      Swal.fire({
        icon: 'success',
        title: 'Created!',
        text: 'Department created successfully.'
      });
      $('#departmentForm')[0].reset();
      $('#previewDeptPic').addClass('d-none').attr('src', '');
      fetchDepartments();
    })
    .fail(function(xhr) {
      Swal.fire({
        icon: 'error',
        title: 'Oops...',
        text: xhr.responseJSON?.message || 'Failed to add department.'
      });
    });
  });


  
  // Fetch departments from server
  function fetchDepartments() {
    $.get('/departments/getAllDepartments', function(data) {
      const deptContainer = $('.dept-data');
      deptContainer.empty();

      if (data.length === 0) {
        deptContainer.append(`
          <div class="text-center text-muted">No departments found.</div>
        `);
        return;
      }

      data.forEach((dept, index) => {
        const shortMessage = dept.description.length > 20 ? dept.description.substring(0, 20) + '...' : dept.description;
        const imageUrl = dept.imageUrl || '/dashboard-assets/images/products/s1.jpg';

        deptContainer.append(`
          <div class="d-flex align-items-center justify-content-between p-3 mb-3 border rounded shadow-sm">
            <div class="d-flex align-items-center gap-3">
              <img src="${imageUrl}" alt="Dept Img"
                class="rounded-circle border"
                style="width: 50px; height: 50px; object-fit: cover;">
              <div>
                <div class="fw-bold text-black">${dept.name}</div>
                <div class="text-muted small">${shortMessage || 'None'}</div>
              </div>
            </div>
            <div>
              <a href="javascript:void(0)" class="text-primary me-2 edit-dept" data-id="${dept._id}">
                <i class="ti ti-edit fs-5"></i>
              </a>
              <a href="javascript:void(0)" class="text-danger delete-dept" data-id="${dept._id}">
                <i class="ti ti-trash fs-5"></i>
              </a>
            </div>
          </div>
        `);
      });
    });
  }




  // Update Departments. Open edit modal or inline form for department
  $(document).on('click', '.edit-dept', function () {
    const id = $(this).data('id');

    // Fetch department info
    $.get('/departments/getSingleDepartment/' + id, function (dept) {
      $('#editDeptId').val(dept._id);
      $('#editDeptName').val(dept.name);
      $('#editDeptDesc').val(dept.description || '');

      if (dept.imageUrl) {
        $('#currentDeptPic').attr('src', dept.imageUrl);
      }

      $('#editModal').modal('show');
    });
  });


  // Preview for Edit
  $('#editDeptPic').on('change', function (event) {
    const preview = $('#previewEditDeptPic');
    const file = event.target.files[0];
    if (file) {
      preview.attr('src', URL.createObjectURL(file)).removeClass('d-none');
    } else {
      preview.addClass('d-none').attr('src', '');
    }
  });
  


  // Edit department form submit
  $('#editDeptForm').on('submit', function(e) {
    e.preventDefault();

    const id = $('#editDeptId').val();
    const form = $('#editDeptForm')[0];
    const formData = new FormData(form);

    const name = $('#editDeptName').val().trim();
    if (!name) {
      return Swal.fire({
        icon: 'warning',
        title: 'Missing Field',
        text: 'Department name is required.'
      });
    }

    Swal.fire({
      title: 'Updating Department...',
      text: 'Please wait ...',
      allowOutsideClick: false,
      didOpen: () => {
        Swal.showLoading();
      }
    });

    $.ajax({
      url: '/departments/updateSingleDepartment/' + id,
      method: 'PUT',
      data: formData,
      processData: false,
      contentType: false,
    })
    .done(() => {
      $('#editModal').modal('hide');
      Swal.fire('Updated!', 'Department updated successfully.', 'success');
      $('#previewEditDeptPic').addClass('d-none').attr('src', '');
      fetchDepartments();
    })
    .fail((xhr) => {
      Swal.fire('Error', xhr.responseJSON?.message || 'Failed to update department.', 'error');
    });
  });





  // Delete department
  $(document).on('click', '.delete-dept', function() {
    const id = $(this).data('id');
    Swal.fire({
      title: 'Are you sure?',
      text: "This will permanently delete the department.",
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#d33',
      cancelButtonColor: '#3085d6',
      confirmButtonText: 'Yes, delete it!'
    }).then(result => {
      if (result.isConfirmed) {
        Swal.fire({
          title: 'Deleting Department...',
          text: 'Please wait ...',
          allowOutsideClick: false,
          didOpen: () => {
            Swal.showLoading(); // shows spinner
          }
        });
        $.ajax({
          url: '/departments/deleteSingleDepartment/' + id,
          type: 'DELETE'
        })
        .done(() => {
          fetchDepartments();
          Swal.fire(
            'Deleted!',
            'Department has been deleted.',
            'success'
          );
        })
        .fail(xhr => {
          Swal.fire({
            icon: 'error',
            title: 'Error',
            text: xhr.responseJSON?.message || 'Failed to delete department.'
          });
        });
      }
    });
  });
});















